import React, { useState } from 'react';
import GoogleLoginButton from './GoogleLoginButton';
import NoteUpload from './NoteUpload';
import ActivitySelector from './ActivitySelector';
import GeneratedActivity from './GeneratedActivity';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [activity, setActivity] = useState(null);
  const [questionCount, setQuestionCount] = useState(0);

  const handleLoginSuccess = (credentialResponse) => {
    setUser(credentialResponse);
  };

  const handleExtractedText = (text) => {
    setExtractedText(text);
  };

  const handleActivitySelect = (selectedActivity, count) => {
    setActivity(selectedActivity);
    setQuestionCount(count);
    // Next: Generate quiz/flashcards/game based on extractedText, selectedActivity, and count
  };

  return (
    <div className="app-container">
      <h1>StudyAI</h1>
      <p>Upload your notes, generate quizzes, flashcards, or games!</p>
      {!user ? (
        <GoogleLoginButton onLoginSuccess={handleLoginSuccess} />
      ) : (
        <div className="activity-section">
          <p>Welcome! You are logged in.</p>
          <NoteUpload onExtractedText={handleExtractedText} />
          {extractedText && !activity && (
            <ActivitySelector extractedText={extractedText} onSelect={handleActivitySelect} />
          )}
          {activity && (
            <div className="generated-section">
              <GeneratedActivity activity={activity} text={extractedText} count={questionCount} />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
